import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import {AiOutlinePlus} from "react-icons/ai"
import { Button } from "./Button";
import AddItem from "./AddItem";

const Account = () => {

    const {username} = useParams()
    const [users, setUsers] = useState('')
    const [products, setProducts] = useState('')
    const [prices, setPrices] = useState('')
    const [quantity, setQuantities] = useState('')
    const [names, setNames] = useState('')
    const [images, setImages] = useState('')

    useEffect(()  => {
        const response = fetch('http://localhost:8000/users')
        const data = response.json()
        setUsers(data)
        console.log("all users: ", data)
        {users&&users.map(()=> {
            if(users.username == username){
                setProducts(users.products)
                setPrices(users.prices)
                setQuantities(users.quantity)
                setNames(users.names)
                setImages(users.images)
            }
        })}
        console.log("products", products)
        console.log("prices: ", prices)
        console.log("Quantities", quantity)
        console.log("Names: ", names)
        console.log("Images: ", images)
    }, [])

    return ( 
        <div className="account">
            <h1>Account - {username}</h1>
            <div className="buttonDiv">
                <button className="button" onClick={console.log("huh")}>+</button>
            </div>
        </div>
     );
}
 
export default Account;

