import { useEffect, useState } from "react";

const Home = () => {
    const [users, setUsers] = useState(null)//temporary til we get connected to a db

    const[password, setPassword] = useState('')
    const[username, setUsername] = useState('')
    const[newPassword, setNewPassword] = useState('')
    const[newUsername, setNewUsername] = useState('')
    const[isPending, setIsPending] = useState(false)

    useEffect(() => {//collects all current account for later comparison
        fetch('http://localhost:8000/users').then(response => response.json()).then(data => {
            setUsers(data);    
        })
    }, [])
    
    const handleSignIn = (e) => {
        e.preventDefault();
        setIsPending(true)
        const user = {username, password}
        var found = 0;
        console.log("User will be signed in if pword and username is correct");
        //will take in text from credentials field and compare to users array
        {users && users.map((oneUser) => {
            if(password === oneUser.password || username === oneUser.username){
                if(!found){
                    //pop up
                    found = 1;
                }
            }
        })}
        if(found){
            console.log(users);
            console.log("sign user in somehow, maybe by using route id")
        }
        else{
            alert("Username or password is incorrect")
        }
        setIsPending(false)
}

    const handleSignUp = (e) => {
        //console.log(e)
        e.preventDefault();
        setIsPending(true)
        const user = {newUsername, newPassword}
        var found = 0;
        //console.log("User will be signed in if pword and username is correct");
        //will take in text from credentials field and compare to users array
        //console.log("Should show the users", users)
        {users && users.map((oneUser) => {
            console.log("Should be single user in db", oneUser, oneUser.password, oneUser.username)
            console.log("Should be new user", user)
            if(newPassword === oneUser.password || newUsername === oneUser.username){
                if(!found){
                    //pop up
                    alert("Username or password is already taken")
                    found = 1;
                }
            }
        })}
        console.log(users)
        if(!found){
            fetch('http://localhost:8000/users', {
            method: 'POST', 
            body: JSON.stringify(user),
            headers: {'Content-Type': 'application/json'}
        }).then(() => {
                setUsers(user)
                console.log(users); //maybe display something here
            })
            found = 0;
        }
        setIsPending(false)
    }

    return (  
        <div className="home">
            <h2>Sign In Page</h2>
            <form onSubmit = {handleSignUp}>
                <label>Username</label>
                <input 
                    type = "text"
                    required
                    value = {newUsername}
                    onChange = {(e) => setNewUsername(e.target.value)}
                >
                </input>
                <label>Password</label>
                <input 
                    type = "text"
                    required
                    value = {newPassword}
                    onChange = {(e) => setNewPassword(e.target.value)}
                >
                </input>
                {!isPending && <button>Sign Up</button>}
                {isPending && <button disabled>Adding user...</button>}
            </form>
            <form onSubmit = {handleSignIn}>
                <label>Username</label>
                <input 
                    type = "text"
                    required
                    value = {username}
                    onChange = {(e) => setUsername(e.target.value)}
                >
                </input>
                <label>Password</label>
                <input 
                    type = "text"
                    required
                    value = {password}
                    onChange = {(e) => setPassword(e.target.value)}
                >
                </input>
                {!isPending && <button>Sign In</button>}
                {isPending && <button disabled>Adding user...</button>}
            </form>
        </div>
    );
}
 
export default Home;
