import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import AddItem from "./AddItem";
import './Account.css'

const Account = () => {

    const history = useNavigate()

    const {username} = useParams()

    const [filtered, setFiltered] = useState([])
    const [products, setProducts] = useState([])
    const [searchInput, setSearchInput] = useState("")

    useEffect(()  => {
        fetch('http://localhost:8000/users').then(response => response.json()).then(data => {
            setProducts(data);//seems to be a problem with new users not being registered?    
        })
        setSearchInput(username)
        if(searchInput) { //if get a search input, check if it matches any product
            const filteredSearch = products.filter((prod) => (
                Object.values(prod).join("").toLowerCase().includes(username.toLowerCase())
            ))
            console.log(filteredSearch)
            setFiltered(filteredSearch) //if a match is found, return the result
        } else {
            setFiltered(products) //if no match is found, return everything
        }
    }, [])

    return ( 
        <div className="account">
            <h1>Account - {username}</h1>
            <h2>Items You're Selling</h2>
                <section className="productDisplay">
                    {products.map(({productID, price, quantity , name, img}) =>(
                        <article key={productID} className="box">
                            <img src={img} alt={name} title={name}/>
                            <h3>{name}</h3>
                            <p>price: {price}</p>
                            <p>quantity: {quantity}</p>
                            {/* need to add functions to buttons */}
                            <button className="delete">Delete</button>
                        </article>
                        
                    ))}
                    <button className="button" onClick={() => {history('/AddItem/' + username)}}>+</button>
                </section>
                <div>
                    <button onClick={() => {history('Home/')}}>Log Out</button>
                </div>
        </div>
     );
}
 
export default Account;
