        {name: "Carla", password: "C00lCat", username: "ReactGeniusAlso", userID: 2},
        {name: "Amir Dane", password: "password", username: "Password", userID: 3}
    ])
    const signIn = (newUsername, newPassword)=> {
        var found = 0;
        console.log("User will be signed in if pword and username is correct");
        //will take in text from credentials field and compare to users array
        {users.map((user) => {
            if(user.password === newPassword || user.username === newUsername){
                if(!found){
                    //pop up
                    console.log("Username or password is already taken")
                    found = 1;
                }
            }
        })}
    }
    return (  
        <div className="home">
            <h2>Sign In Page</h2>
            <button onClick = {() => signIn(newUsername, newPassword)}>Sign In</button>
        </div>
    );
}
 
export default Home;
