import styled from "styled-components";
export const Button = styled.button`
    width: 20px;
    height: 20px;
    align-self: center;
    background-color: white;
    align-items: center;
    border: lightgray;
    align-content: center;
    font-size: large;

    &:hover{
        background-color: red;
    }
`;
