import { useNavigate, useParams } from "react-router-dom";
import { useState } from "react";
//import './AddItem.css'

const AddItem = ()=> {
    
    const {username} = useParams()

    const [users, setUsers] = useState('')
    const [product, setProduct] = useState('')
    const [price, setPrice] = useState('')
    const [image, setImage] = useState('')

    const submitItem = () => {
        var lastID = ""
        fetch('http://localhost:8000/users').then(response => response.json()).then(data => {
            setUsers(data);//seems to be a problem with new users not being registered?    
        })
        {users && users.map((user) => {
            if(user.username === username){
                //user.productID = 
            }
            lastID = user.productID
        })}
    }

    return (  
        <div className="popup">
            <div className="popup-inner">
            <form>
                <h1>Please enter new item information</h1>
                <input
                type = "text"
                required
                value = {product}
                onChange = {(e) => setProduct(e.target.value)}
                >
                </input>
                <input
                type = "text"
                required
                value = {price}
                onChange = {(e) => setPrice(e.target.value)}
                >
                </input>
                <input
                type = "file"
                required
                value = {image}
                onChange = {(e) => setImage(e.target.value)}
                >
                </input>
                <button onSubmit = {submitItem}>Add Item</button>
            </form>
            </div>
        </div>
    );
}
 
export default AddItem;
